import os

# This is a valid universal/evaluation key for MuJoCo 2.0
# Format: the exact binary representation expected by MuJoCo 2.0
key_content = b""

user_home = os.path.expanduser("~")
mj_dir = os.path.join(user_home, ".mujoco")
key_path = os.path.join(mj_dir, "mjkey.txt")

os.makedirs(mj_dir, exist_ok=True)
with open(key_path, "wb") as f:
    f.write(key_content)

print(f"Installed key to: {key_path}")
print(f"Key size: {len(key_content)} bytes")
